.socials__link {
  width: 28px;
  height: 28px;
  display: flex;
  border: 1px solid white;
  justify-content: center;
  align-items: center;
  border-radius: 50%;
}

.fa {
  color: abstracts.$white;
  text-decoration: none;
}

