@use 'sass:color';
@use '../abstracts';

.aside {
  position: relative;
  z-index: 2;
  margin-bottom: -14rem;
}

.get__access {
  padding: 2.5rem 1.75rem;
  margin: auto;
  max-width: 53.9375rem;
}
.signup__section {
  margin: 7rem 1.25rem 3rem;
  // padding: 0 1.25rem;
  background-color: abstracts.$dark-grey;
  border-radius: 9px;
}
.signup__title {
  font-size: abstracts.calculateRem(18);
  line-height: 1.5;
  text-align: center;
}
.signup__content {
  font-size: abstracts.calculateRem(14);
  text-align: center;
  line-height: 1.8;
}
.register__email {
  display: block;
  margin: auto;
  font-size: abstracts.calculateRem(10);
  padding: 1rem 2rem;
  border-radius: 24px;
  border: none;
  width: 100%;

  &:hover,
  &:focus {
    cursor: pointer;
    outline: dashed currentColor;
    outline: dashed lighten(abstracts.$white, 80%);
    outline-offset: 0.25em;
  }
}

.email__wrapper {
  position: relative;
}

.error__msg {
  position: absolute;
  bottom: -1.5rem;
  font-size: abstracts.calculateRem(12);
  left: 1rem;
  color: abstracts.$error;
  display: none;
}

.error__none {
  display: block;
}
.correct__format {
  display: block;
  color: lighten(abstracts.$green, 60%);
}

.registration {
  margin-top: 2rem;
}
.get__started--btn {
  margin-top: 2.5rem;
  padding: 1rem 2rem;
  outline: none;
  width: 100%;

  &:hover,
  &:focus {
    cursor: pointer;
    outline: dashed currentColor;
    outline-offset: 0.25em;
  }
}

@media screen and(min-width: 680px) {
  .get__started--btn {
    margin-top: 0;
  }
  .registration {
    display: flex;
    align-items: center;
    gap: 2rem;
    max-width: 44rem;
    margin: 2rem auto;
  }
  .register__email {
    width: 100%;
  }
  .email__wrapper {
    flex: 1 1 40%;
  }
  .signup__content {
    max-width: 42rem;
    margin: 2rem auto;
  }
}

@media screen and(min-width: 1180px) {
  .signup__section {
    margin: 7rem 18rem;
  }
  .aside {
    margin-bottom: -16.5rem;
    margin-top: 15rem;
  }
}
